import React from 'react';
import styles from './Users.module.css';
let Users = (props)=>
{
    props.setUsers( [
        { id: 1, photoUrl:"https://upload.wikimedia.org/wikipedia/commons/8/88/Dmitry_Nagiev_2017_4.jpg", followed: false, fullName: "Anya", status: 'Big boss', location:{city:'Kiev', country:'Ukraine'} },
        { id: 3, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/8/88/Dmitry_Nagiev_2017_4.jpg', followed: true, fullName: "Rusya", status: 'Sebek sytyluj', location:{city:'Kiev', country:'Ukraine'} },
        { id: 3, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/8/88/Dmitry_Nagiev_2017_4.jpg', followed: false, fullName: "Sebek", status: 'Big boss', location:{city:'Kiev', country:'Ukraine'} }
      ])
  return <div>{
  props.users.map(u=><div key = {u.id}> 
  <span>
      <div>
          <img src={u.photoUrl} className={styles.userPhoto}/>
      </div>
      <div>
          {u.followed ? <button onClick={() => {props.unfollow(u.id)}}>Unfollow</button>
           : <button onClick={() => {props.follow(u.id)}}>Follow</button> }}

      </div>
  </span>
  <span>
      <span>
  <div>{u.fullName}</div>
  <div>{u.status}</div>
      </span>
  <span>
      <div>{u.location.country}</div>
      <div>{u.location.city}</div>
</span>
      </span>
      </div>
   )
}
</div>
}

export default Users;
