const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';

let initialState={
    users: [
        { id: 1, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/thumb/8/88/Dmitry_Nagiev_2017_4.jpg', followed: false, fullName: "Anya", status: 'Big boss', location:{city:'Kiev', country:'Ukraine'} },
        { id: 3, followed: true, fullName: "Rusya", status: 'Sebek sytyluj', location:{city:'Kiev', country:'Ukraine'} },
        { id: 3, followed: false, fullName: "Sebek", status: 'Big boss', location:{city:'Kiev', country:'Ukraine'} }
      ]
}

const usersReducer = (state = initialState, action) => {
    switch(action.type){
        case FOLLOW:
            return {...state, 
                users:state.users.map(u=>{
                    if(u.id===action.userId){
                        return {...u, followed:true}
                    }
                    return u;
                })
                // or users:[...state.users]
            }
        case UNFOLLOW:
           return {...state, 
                users:state.users.map(u=>{
                    if(u.id===action.userId){
                        return {...u, followed:false}
                    }
                    return u;
                })
                // or users:[...state.users]
            }

        case SET_USERS:{
        return{
            ...state, users:[...state.users, ...action.users] //склеиваем новых и старых юзеров в один массив 
        }
        }
        default: 
        return state;
    }
}

export const followAC = (userId) => ({type:FOLLOW, userId})
export const unfollowAC = (userId) => ({type:UNFOLLOW, userId})
export const setUsersAC = (users) =>({type:SET_USERS, users})
export default usersReducer;
