import React from 'react';
import {connect} from "react-redux";
import { followAC, unfollowAC, setUsersAC, setCurrentPageAC, setUsersTotalCountAC, toggleIsFetchingAC } from '../redux/users-reducer';
import * as axios from 'axios';
import Users from './Users';
import Preloader from '../components/common/Preloader/Preloader';


class UsersContainer extends React.Component {
componentDidMount(){
    this.props.toggleIsFetching(true);
    axios
    .get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
    .then((response) => {
    this.props.toggleIsFetching(false);
      this.props.setUsers(response.data.items);
      this.props.setTotalUsersCount(response.data.totalCount);
    });
}
     onPageChanged = (pageNumber)=> {
       this.props.setCurrentPage(pageNumber);
       this.props.toggleIsFetching(true);
       axios
    .get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
    .then((response) => {
        this.props.toggleIsFetching(false);

      this.props.setUsers(response.data.items);
    });
     }

  render() {
    return <> 
    {this.props.isFetching ? <Preloader/> : null}
    <Users totalUsersCount={this.props.totalUsersCount}
    pageSize={this.props.pageSize}
    currentPage={this.props.currentPage}
    onPageChanged={this.onPageChanged}
    users={this.props.users}
    follow={this.props.follow}
    unfollow={this.props.unfollow}


    />
    </>
  }}

let mapStateToProps = (state) =>{
    return {
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount:state.usersPage.totalUsersCount,
        currentPage:state.usersPage.currentPage, 
        isFetching: state.usersPage.isFetching
    }
}

let mapDispatchToProps = (dispatch)=>{//переадет колбеки, которая презантационная компонента может вызывать
    return{
        follow: (userId)=>{
            dispatch(followAC(userId));

        },
        unfollow: (userId)=>{
            dispatch(unfollowAC(userId));
        },
        setUsers: (users)=>{
            dispatch(setUsersAC(users));
        },
        setCurrentPage:(pageNumber)=>{
            dispatch(setCurrentPageAC(pageNumber));
        },
        setTotalUsersCount:(totalCount)=>{
            dispatch(setUsersTotalCountAC(totalCount));
        },
        toggleIsFetching:(isFetching)=>{
            dispatch(toggleIsFetchingAC(isFetching));
        }

    }
}

export default connect(mapStateToProps, {
    follow:followAC,
    unfollow:unfollowAC,
    setUsers:setUsersAC,
    setCurrentPage:setCurrentPageAC,
    setTotalUsersCount:setUsersTotalCountAC,
    toggleIsFetching:toggleIsFetchingAC
    })(UsersContainer);
